package meu.pacote;

public class LacosDeRepeticao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Contagem de 1 a 10 com for: ");
		for (int i =1; i<=10; i++) {
			System.out.println(i);
		}
		
		//Com while
		
		System.out.println("\nContagem de 10 a 1 com while");
		
		int j = 10;
		while (j >=1) {
			System.out.println(j);
			j--; 
		}

	}

}
